		int B = sc.nextInt();
		int C = sc.nextInt();
		int D = sc.nextInt();
		for (int i = 0; i < 1000; i++)	{
			visited.add((long) (B + i * A));
		}
		for (int j = 0; j < 1000; j++)	{
			long check = (long) D + j * C;
			if (visited.contains(check))	{
				System.out.println(check);
				System.exit(0);
			}
		}
		System.out.println(-1);

	}
