		int d = in.nextInt();
		if (b == d) {
			out.print(b);
			return;
		}

		if (b < d) {
			int tmp = b;
			b = d;
			d = tmp;

			tmp = a;
			a = c;
			c = tmp;
		}
		// b>= d
		int res = d;
		b -= d;
		// d == 0 b > 0
		for (int i = 0; i < c; i++) {
			if ((i * a + b) % c == 0) {
