using namespace std;
void swap(int &a, int &b) {
  a = a ^ b;
  b = a ^ b;
  a = a ^ b;
}
int gcd(int a, int b) {
  int temp;
  if (a > b) swap(a, b);
  while (true) {
    if (a == 0) return b;
    temp = a;
    a = b % a;
    b = temp;
  }
}
int main() {
  int a, b, c, d;
  cin >> b >> a >> d >> c;
  int s = gcd(b, d);
  if (abs(a - c) % s != 0) {
    cout << -1 << endl;
    return 0;
  }
  int m1 = a;
  int m2 = c;
  while (m1 != m2) {
    m1 < m2 ? m1 += b : m2 += d;
  }
  cout << m1 << endl;
  return 0;
}

