	long sqrt(long m){
		long l = 0;
		long r = 1000000000+9;
		while(r-l>1){
			long mid = (r+l)/2;
			if(mid*mid>m){
				r = mid;
			}else{
				l=mid;
			}
		}
		return l;
	}
	int countPow(int m,int n){
		int ans = 0;
		while(m%n==0&&m>0){
			ans++;
			m/=n;
		}
		return ans;
	}
	long binPow(long a, long b, long m){
		if(b == 0){
			return 1%m;
		}
		if(b%2==1){
			return a%m*binPow(a,b-1,m)%m;
		}else{
			long c = binPow(a,b/2,m);
			return c*c%m;
		}

	}
	long gcd(long a, long b){
