        int d = Integer.parseInt(inputs[1]);

        if(b == d) {System.out.println(b);return;}
        int diff = Math.abs(b-d);
        int gcd = GCD(a,c);
        if(diff%gcd != 0) {System.out.println("-1");return;}
        if(b<d) {
            int temp = b; b = d; d = temp;
            temp = a; a = c; c =  temp;
        }
        for(;;) {
            if((b-d)%c == 0) break;
            b += a;
        }
        System.out.println(b);
    }
}

