    cout << -1 << endl;
  else {
    t = c / g;
    x = x * pro / g;
    x = (x % t + t) % t;
    ans = b + x * a;
    lcm = a * c / g;
    while (ans < ma) ans += lcm;
    cout << ans << endl;
  }
  return 0;
}

