	{

	 Scanner sc=new Scanner(System.in);
	 int n=sc.nextInt();

	 int[] a=new int[n];
	 for(int i=0;i<n;i++)a[i]=sc.nextInt();

	 int[] b=new int[n];

	 System.arraycopy(a,0,b,0,n);

	 for(int i=0;i+1<n;i++){
	  if(Math.abs(a[i+1]-a[i])>=2){
