public class Main {

    void solve() throws IOException {
        int n = readInt();
        int[] a = readIntArray(n);
        int[] ind = new int[n];
        ArrayList<Integer> list = new ArrayList<>();
        ArrayList<Integer> sorted = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            list.add(a[i]);
        }
        for (int inr = 0; inr < n; inr++) {
            for (int i = 1; i < list.size(); i++) {
                if (Math.abs(list.get(i) - list.get(i - 1)) > 1) {
                    out.print("NO");
                    return;
                }
            }
            int max = -1;
            for (int i : list) {
                max = maxInt(max, i);
            }
            for (int i = 0; i < n; i++) {
                if (list.get(i) == max) {
                    sorted.add(list.get(i));
                    list.remove(i);
                    break;
                }
            }
        }
        Arrays.parallelSort(a);
        Collections.reverse(sorted);
        for (int i = 0; i < n; i++) {
            if (a[i] != sorted.get(i)) {
                out.print("NO");
                return;
            }
        }
        out.print("YES");
    }

    boolean sorted(int[] a, int n) {
        int[] b = a.clone();
        Arrays.sort(b);
        boolean ans = true;
        for (int i = 0; i < n; i++) ans &= a[i] == b[i];
        return ans;
    }

    public static void main(String[] args) throws IOException {
        new Main().run();
    }

