public class C {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int rav = sc.nextInt();
        ArrayList<Integer> raviolis = new ArrayList<>();
        ArrayList<Integer> clone = new ArrayList<>();
        while(rav-- > 0){
            int n = sc.nextInt();
            raviolis.add(n);
            clone.add(n);
        }
        ArrayList<Integer> sorted = new ArrayList<>();
        while(raviolis.size() > 0){
            for(int i=0;i<raviolis.size()-1;i++){
                int diff = Math.abs(raviolis.get(i)-raviolis.get(i+1));
                if(diff >= 2){
                    if(raviolis.get(i) > raviolis.get(i+1)){
                        raviolis.set(i,raviolis.get(i)-diff/2);
                        raviolis.set(i+1,raviolis.get(i+1)+(diff+1)/2);
                    }
                    else if(raviolis.get(i) < raviolis.get(i+1)){
                        raviolis.set(i+1,raviolis.get(i+1)-diff/2);
                        raviolis.set(i,raviolis.get(i)+(diff+1)/2);
                    }
                }
            }
            int maxindex = -1;
            int maxsize = 0;
            for(int i=0;i<raviolis.size();i++){
                if(raviolis.get(i) > maxsize){
                    maxsize = raviolis.get(i);
                    maxindex = i;
                }
            }
            int k = raviolis.remove(maxindex);
            sorted.add(k);
        }
        Collections.reverse(sorted);
        Collections.sort(clone);
        System.out.println(sorted.equals(clone) ? "YES" : "NO");
    }
}

