  vector<long long> v(second.length() + 1, 0);
  long long cur = 0;
  for (int j = 2; j < second.length(); j++) {
    while (cur != 0 && second[cur] != second[j - 1]) cur = v[cur];
    if (second[cur] == second[j - 1]) cur++;
    v[j] = cur;
  }
}
