vector<int> vec;
int front(int x) {
  int ans = 0, prev = vec[x];
  for (int i = x + 1; i < vec.size(); i++)
    if (vec[i] <= prev)
      ans++, prev = vec[i];
    else
      break;
  return ans;
}
int back(int x) {
  int ans = 0, prev = vec[x];
  for (int i = x - 1; i >= 0; i--)
    if (vec[i] <= prev)
      ans++, prev = vec[i];
    else
      break;
  return ans;
}
