			int max = 0;
			for(int i : list) {
				max = Math.max(max, i);
			}
			int idx = -1;
			for(int i=0;i<list.size();i++){
				if(list.get(i)==max){
					idx = i;
					break;
				}
			}

			for(int i=0;i<list.size();i++){
