  for (int i = (1); i < (a.size()); i++) {
    if (abs(a[i] - a[i - 1]) > 1) return 0;
  }
  pair<long long, long long> p = pair<long long, long long>(a[0], 0);
  for (int i = (0); i < (a.size()); i++) {
    p = max(p, pair<long long, long long>(a[i], -i));
