void dfs(long long s, vector<bool> &vis, vector<vector<long long>> &adlist) {
  if (vis[s]) return;
  vis[s] = true;
  for (auto x : adlist[s]) {
    if (vis[x] == false) {
      cout << s << " " << x << "\n";
    }
    dfs(x, vis, adlist);
  }
}
void bfs(long long x, vector<bool> &vis, vector<vector<long long>> &adlist) {
  queue<long long> q;
  vis[x] = true;
  q.push(x);
  while (!q.empty()) {
    long long s = q.front();
    q.pop();
    for (auto u : adlist[s]) {
      if (vis[u]) continue;
      vis[u] = true;
      q.push(u);
    }
  }
}
bool bpchk(vector<vector<long long>> &adj, vector<long long> &color) {
  color[1] = 1;
  queue<long long> q;
  q.push(1);
  while (!q.empty()) {
    long long u = q.front();
    q.pop();
    for (auto x : adj[u]) {
      if (color[x] == -1) {
        color[x] = color[u] ^ 1;
        q.push(x);
      }
    }
  }
  for (long long i = 1; i < adj.size(); i++) {
    for (auto x : adj[i]) {
      if (color[x] == color[i]) return false;
    }
  }
  return true;
