def check_ravioli(arr, n):
    for i in range(n):
        if i > 0 and abs(arr[i]-arr[i-1]) >= 2:
            return False
        elif i < n-1 and abs(arr[i] - arr[i+1]) >= 2:
            return False
    return True


def ravioli_sort(arr, n):
    if n == 0:
        return True
    if not check_ravioli(arr, n):
        return False
    m = -1
    m_idx = -1
    for i in range(n):
        if arr[i] > m:
            m = arr[i]
            m_idx = i

    arr.append(m)
    del arr[m_idx]
    return ravioli_sort(arr, n-1)


