import java.io.*;
import java.util.*;


public class Solution
{

    static class InputReader {
    private InputStream stream;
    private byte[] buf = new byte[1024];

    private int curChar;

    private int numChars;

    public InputReader(InputStream stream) {
        this.stream = stream;
    }

    public int read() {
        if (numChars == -1)
            throw new RuntimeException();
        if (curChar >= numChars) {
            curChar = 0;
            try {
                numChars = stream.read(buf);
            } catch (IOException e) {
                throw new RuntimeException();
            }
            if (numChars <= 0)
                return -1;
        }
        return buf[curChar++];
    }

    public String readString() {
        final StringBuilder stringBuilder = new StringBuilder();
        int c = read();
        while (isSpaceChar(c))
            c = read();
        do {
            stringBuilder.append(c);
            c = read();
        } while (!isSpaceChar(c));
        return stringBuilder.toString();
    }

    public int readInt() {
        int c = read();
        while (isSpaceChar(c))
            c = read();
        int sgn = 1;
        if (c == '-') {
            sgn = -1;
            c = read();
        }
        int res = 0;
        do {
            res *= 10;
            res += c - '0';
            c = read();
        } while (!isSpaceChar(c));
        return res * sgn;
    }

    public long readLong() {
        int c = read();
        while (isSpaceChar(c))
            c = read();
        int sgn = 1;
        if (c == '-') {
            sgn = -1;
            c = read();
        }
        long res = 0;
        do {
            res *= 10;
            res += c - '0';
            c = read();
        } while (!isSpaceChar(c));
        return res * sgn;
    }

    public boolean isSpaceChar(int c) {
        return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
    }
    }


    public static void main(String[] args) throws Exception{
        InputReader reader = new InputReader(System.in);

        int n  =reader.readInt();
        int[] arr = new int[n];
        int[] sorted_array = new int[n];
        for(int i = 0;i<n;i++){
            arr[i] = reader.readInt();
            sorted_array[i] = arr[i];
        }
        Arrays.sort(sorted_array);
        int sorted_pointer =n-1;
        Set<Integer> set = new HashSet();
        boolean flag = false;
        while(sorted_pointer>= 0){
            int top_most  = sorted_array[sorted_pointer];
            int index = search(arr, top_most, set);
            if(index == -1){
                System.out.println("NO");
                flag = true;
                break;
            }
            if(index == 0)
            {

                if(n > 1 && top_most - arr[index+1] >= 2)
                {
                    System.out.println("NO");
                    flag = true;
                    break;
                }
                else
                {
                    set.add(index);
                    if(top_most  == sorted_array[sorted_pointer]){
                        sorted_pointer--;
                        continue;
                    }
                    else {
                        System.out.println("NO");
                    }
                }
            }

            else if(index == n-1)
            {
                if(top_most - arr[index-1] >= 2)
                {
                    System.out.println("NO");
                    flag = true;
                    break;
                }
                else
