public class April1C {

    private static BufferedReader in;
    private static BufferedWriter out;

    public static void main(String[] args) throws IOException {
        open();

        int n = readInt();
        int[] x = readInts();
        int[] c = new int[n];
        for (int i = 0; i < n; i++) {
            c[i] = x[i];
        }

        Arrays.sort(c);

        if (n == 1) {
            System.out.println("YES");
        } else {
            int last = n - 1;
            while (last >= 0) {
                boolean changed = false;
                while (!changed) {
                    changed = true;
                    for (int i = 0; i < last; i++) {
                        if (x[i] - 2 >= x[i + 1]) {
                            int sum = (x[i] + x[i + 1]);
                            x[i + 1] = sum / 2;
                            x[i] = sum / 2 + sum % 2;
                            changed = false;
                            break;
                        } else if (x[i + 1] - 2 >= x[i]) {
                            int sum = (x[i] + x[i + 1]);
                            x[i] = sum / 2;
                            x[i + 1] = sum / 2 + sum % 2;
                            changed = false;
                            break;
                        }
                    }
                }

                int max = x[last];
                for (int i = last - 1; i >= 0; i--) {
                    if (max <= x[i]) {
                        max = x[i];
                        int t = x[last];
                        x[last] = x[i];
                        x[i] = t;
                    }
                }

                last--;
            }

            boolean yes = true;
            for (int i = 0; i < n; i++) {
                if (x[i] != c[i]) {
                    yes = false;
                    break;
                }
            }

            if (yes) {
                System.out.println("YES");
            } else {
                System.out.println("NO");
            }

        }
        close();
    }

    private static int[] readInts() throws IOException {
        return Stream.of(in.readLine().split(" ")).mapToInt(Integer::parseInt).toArray();
    }

    private static int readInt() throws IOException {
        return Integer.parseInt(in.readLine());
    }

    private static long[] readLongs() throws IOException {
        return Stream.of(in.readLine().split(" ")).mapToLong(Long::parseLong).toArray();
    }

    private static long readLong() throws IOException {
        return Long.parseLong(in.readLine());
    }

    private static double[] readDoubles() throws IOException {
        return Stream.of(in.readLine().split(" ")).mapToDouble(Double::parseDouble).toArray();
    }

    private static double readDouble() throws IOException {
        return Double.parseDouble(in.readLine());
    }

    private static String readString() throws IOException {
        return in.readLine();
    }

    private static void open() {
        in = new BufferedReader(new InputStreamReader(System.in));
        out = new BufferedWriter(new OutputStreamWriter((System.out)));
    }

    private static void close() throws IOException {
        out.flush();
        out.close();
        in.close();
    }

}

