public class RavioliSort {
    public RavioliSort() {
        super();
    }

    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);

        int n = in.nextInt();
        List<Integer> ar = new ArrayList<Integer>();

        for(int i=0; i<n; i++) {
          int x = in.nextInt();
          ar.add(x);
        }

        boolean ans = true;

        for(int i=0; i<n; i++) {
            int max = 0;
            int limit = ar.size();
            for(int j=0; j<limit; j++) {
                if(j < limit-1 ) {                
                    if( (int)Math.abs( ar.get(j).intValue() - ar.get(j+1).intValue() ) >= 2 ) {
                        ans = false;
                        break;
                    }
                }

                if(ar.get(j).intValue() > ar.get(max) ) {
                    max = j;
                }
            }

            if(ans == false) {
                break;
            }

            ar.remove(max);
        }

        System.out.println( (ans?"YES":"NO")  );
    }
}

