int main() {
  int err = 0;
  int t;
  scanf("%d", &t);
  int y;
  t--;
  vector<int> st;
  priority_queue<int> pq;
  scanf("%d", &y);
  st.push_back(y);
  pq.push(y);
  while (t--) {
    int x;
    scanf("%d", &x);
    st.push_back(x);
    pq.push(x);
    if (abs(x - y) >= 2) err = 1;
    y = x;
  }
  if (err) {
    puts("NO");
    return 0;
  }
  while (!err && st.size() > 2) {
    int idx;
    for (int i = 0; i < st.size(); i++) {
      if (st[i] == pq.top()) {
        if (i - 1 >= 0 && i + 1 < st.size()) {
          if (abs(st[i - 1] - st[i + 1]) >= 2) err = 1;
        }
        st.erase(st.begin() + i);
        break;
      }
    }
    pq.pop();
  }
  if (err)
    puts("NO");
  else
    puts("YES");
  return 0;
}

