bool can_slide(vector<int> &A) {
  for (int i = 0; i + 1 < A.size(); i++) {
    if (abs(A[i] - A[i + 1]) >= 2) return 1;
  }
  return 0;
}
int n;
vector<int> A, B;
int main() {
  cin >> n;
  A.resize(n);
  for (int x = 0; x < n; x++) cin >> A[x];
  if (can_slide(A)) {
    cout << "NO\n";
    exit(0);
  }
  for (int x = 0; x < n; x++) {
    int idx = 0;
    vector<int> tmp;
    for (int y = 0; y < A.size(); y++)
      if (A[y] > A[idx]) idx = y;
    B.push_back(A[idx]);
    for (int y = 0; y < A.size(); y++)
      if (idx != y) tmp.push_back(A[y]);
    A = tmp;
    if (can_slide(B) || can_slide(A)) {
      cout << "NO\n";
      exit(0);
    }
  }
  cout << "YES\n";
}

