public class CF952C {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		boolean slid = false;
		int l = sc.nextInt();
		ArrayList<Integer> arr = new ArrayList<>();
		ArrayList<Integer> sorted = new ArrayList<>();
		for(int i=0; i<l; i++) arr.add(sc.nextInt());
		for(int x=0; x<l; x++) {
			if(hasDiff(arr) || hasDiff(sorted)) {
				slid = true;
				break;
			}
			int index = getMax(arr);
			sorted.add(arr.get(index));
			arr.remove(index);
		}
		System.out.println(slid ? "NO" : "YES");
	}

	static int getMax(ArrayList<Integer> arr) {
		int index = -1;
		int max = -1;
		for(int i=0; i<arr.size(); i++)
			if(arr.get(i)>max) {
				max = arr.get(i);
				index = i;
			}
		return index;
	}

	static boolean hasDiff(ArrayList<Integer> arr) {
		for(int i=1; i<arr.size(); i++) 
			if(Math.abs(arr.get(i)-arr.get(i-1))>=2) return true; 
		return false;
	}
}

