	public static void slide(int[] a, int max) {
		for (int i = 0; i < max; i++) {
			while (i < max - 1 && a[i] > a[i + 1] + 1) {
				a[i + 1]++;
				a[i]--;
			}
			while (i > 0 && a[i] > a[i - 1] + 1) {
				a[i - 1]++;
				a[i]--;
			}
		}
	}

	public static int[] sort(int[] a) {
		int idx = 0;
		for(int i = 0; i < a.length; i++) {
			if(a[i]>a[idx])idx=i;
		}
		int[] aa = new int[a.length-1];
		int i = 0;
		for(int j = 0; j < a.length; j++) {
			if(j==idx)continue;
			aa[i++]=a[j];
		}
		a=aa;
		return a;
	}
	public static int highest(int[] a) {
		int idx = 0;
		for(int i = 0 ; i < a.length; i++) {
			if(a[i]>a[idx])idx=i;
		}
		return idx;
	}


}

