import java.util.HashMap;
import java.util.InputMismatchException;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;
import java.util.Map;
import java.io.Writer;
import java.io.OutputStreamWriter;
import java.io.InputStream;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 *
 * @author Roy
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        OutputWriter out = new OutputWriter(outputStream);
        TaskC solver = new TaskC();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskC {
        public void solve(int testNumber, InputReader in, OutputWriter out) {

            Integer x, val;
            Integer n = in.readInt();
            List<Integer> ravioli = new ArrayList<>();
            List<Integer> outputArray = new ArrayList<>();

            Map<Integer, Integer> counter = new HashMap<>();
            Map<Integer, Integer> counter2 = new HashMap<>();

            for (int i = 0; i < n; i++) {
                x = in.readInt();
                ravioli.add(x);

                val = counter.get(x) == null ? 1 : counter.get(x) + 1;
                counter.put(x, val);
            }

            int max = -1, indx = 0;
            for (int i = 0, diff = 0; i < n; i++) {
                max = -1;
                indx = 0;
                for (int j = 0, sz = ravioli.size(); j < sz; j++) {
                    if (j != sz - 1) diff = Math.abs(ravioli.get(j) - ravioli.get(j + 1));

                    if (j != sz - 1 && diff > 1 && ravioli.get(j) > ravioli.get(j + 1)) {
                        ravioli.set(j, ravioli.get(j) - 1);
                        ravioli.set(j + 1, ravioli.get(j + 1) + 1);
                    }

                    if (j != sz - 1 && diff > 1 && ravioli.get(j) < ravioli.get(j + 1)) {
                        ravioli.set(j, ravioli.get(j) + 1);
                        ravioli.set(j + 1, ravioli.get(j + 1) - 1);
                    }

                    if (ravioli.get(j) > max) {
                        indx = j;
                        max = ravioli.get(j);
                    }
                }
                outputArray.add(max);
                ravioli.remove(indx);
            }

            for (int i = 0; i < outputArray.size(); i++) {
                x = outputArray.get(i);
                val = counter2.get(x) == null ? 1 : counter2.get(x) + 1;

                counter2.put(x, val);
            }
            boolean flag = true;

            for (Integer key : counter.keySet()) {
                val = counter.get(key);

                if (counter2.get(key) != val) {
                    flag = false;
                    break;
                }
            }

            if (flag) {
                out.print("YES");
            } else {
                out.print("NO");
            }
        }

    }

    static class InputReader {
        private InputStream stream;
        private byte[] buf = new byte[1024];
        private int curChar;
        private int numChars;
        private InputReader.SpaceCharFilter filter;

        public InputReader(InputStream stream) {
            this.stream = stream;
        }

        public int read() {
            if (numChars == -1) {
                throw new InputMismatchException();
            }
            if (curChar >= numChars) {
                curChar = 0;
                try {
                    numChars = stream.read(buf);

                } catch (IOException e) {
                    throw new InputMismatchException();
                }
                if (numChars <= 0) {
                    return -1;
                }
            }
            return buf[curChar++];
        }

        public int readInt() {
            int c = read();
            while (isSpaceChar(c)) {
                c = read();
            }
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            int res = 0;
            do {
                if (c < '0' || c > '9') {
                    throw new InputMismatchException();
                }
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public boolean isSpaceChar(int c) {
            if (filter != null) {
                return filter.isSpaceChar(c);
            }
            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }

        public interface SpaceCharFilter {
            public boolean isSpaceChar(int ch);

        }

    }

    static class OutputWriter {
        private final PrintWriter writer;

        public OutputWriter(OutputStream outputStream) {
            writer = new PrintWriter(new BufferedWriter(new OutputStreamWriter(outputStream)));
        }

        public OutputWriter(Writer writer) {
            this.writer = new PrintWriter(writer);
        }

        public void print(Object... objects) {
            for (int i = 0; i < objects.length; i++) {
                if (i != 0) {
                    writer.print(' ');
                }
                writer.print(objects[i]);
            }
        }

        public void close() {
            writer.flush();
            writer.close();
        }

    }
}

import java.util.*;

public class Main 
{
    public static void main(String[] args) 
    {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        ArrayList<Integer> ar = new ArrayList<Integer>();
        for (int i = 0; i < n; i++)
            ar.add(scanner.nextInt());
        for (int i = 1; i < n; i++)
            if (Math.abs(ar.get(i) - ar.get(i - 1)) >= 2)
            {
                System.out.println("NO");
                return;
            }

        for (int i = 0; i < n - 1; i++)
        {
            int P = min(ar);
            if (P > 0 && P < ar.size() - 1) 
                if (Math.abs(ar.get(P + 1) - ar.get(P - 1)) >= 2)
                {
                    System.out.println("NO");
                    return;
                }
            ar.remove(P);
        }
        System.out.println("YES");
    }

    public static int min(ArrayList<Integer> ar)
    {
        int minV = ar.get(0), minP = 0;
        for (int i = 1; i < ar.size(); i++)
            if (ar.get(i) < minV)
            {
                minV = ar.get(i);
                minP = i;
            }

        return minP;
    }
}

