				if (a.get(i) > max) {
					ind = i;
					max = a.get(i);
				}
			}
			if (ind != 0 && ind != a.size() - 1 && Math.abs(a.get(ind + 1) - a.get(ind - 1)) > 1) {
				System.out.println("NO");
				return;
			}
			a.remove(ind);
		}
		System.out.println("YES");
