        Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        ArrayList<Integer> arr = new ArrayList<>(n);
        ArrayList<Integer> arrtmp = new ArrayList<>(n);
        for(int i = 0;i < n;i++) {
        	arr.add(in.nextInt());
        	arrtmp.add(arr.get(i));
        }


        int[] ans = new int[n];
        int ind = 0;
        while(arr.size() > 0) {
        	for(int i = 1;i < n - 1;i++) {
        		if(arr.get(i) - arr.get(i - 1) > 1) {
        			arr.set(i, arr.get(i) - 1);
        			arr.set(i, arr.get(i - 1) + 1);
        		} else if(arr.get(i - 1) - arr.get(i) > 1) {
        			arr.set(i, arr.get(i - 1) - 1);
        			arr.set(i, arr.get(i - 1) + 1);
        		}
        	}
        	int index = 0;
        	int val = Integer.MIN_VALUE;
        	for(int i = 0;i < n;i++)
        		if(arr.get(i) > val) {
        			val = arr.get(i);
        			index = i;
        		}
        	ans[ind++] = val;
        	n--;
        	arr.remove(index);
        }
        Collections.sort(arrtmp);
        Collections.reverse(arrtmp);
        for(int i = 0;i < ans.length;i++) {
        	if(ans[i] != arrtmp.get(i)) {
        		System.out.println("NO");
        		return;
        	}
//        	System.out.println(ans[i]);
        }
        System.out.println("YES");
    }
}

