using ll = long long;
bool RS(const vector<ll>& A) {
  if (A.size() == 1) {
    return true;
  }
  for (ll i = 0; i + 1 < A.size(); i++) {
    if (std::abs(A[i] - A[i + 1]) >= 2) {
      return false;
    }
  }
  ll mi = 0;
  for (ll i = 1; i < A.size(); i++) {
    if (A[i] > A[mi]) {
      mi = i;
    }
  }
  vector<ll> A2;
  for (ll i = 0; i < A.size(); i++) {
    if (i != mi) {
      A2.push_back(A[i]);
    }
  }
  return RS(A2);
}
int main() {
  ll n;
  cin >> n;
  vector<ll> A(n, 0);
  for (ll i = 0; i < n; i++) {
    cin >> A[i];
  }
  cout << (RS(A) ? "YES" : "NO") << endl;
}

