import java.io.*;
import java.util.*;


public class Alpha {
    String filename = "subseq";//filename here, System.in/out if no file

    FastScanner in;
    PrintWriter out;

    void pass() {
    }

    int[] rem(int[] a, int x) {
        int[] ans = new int[a.length - 1];
        for (int i = 0; i < x; i++) ans[i] = a[i];
        for (int i = x; i < a.length - 1; i++) ans[i] = a[i + 1];
        return ans;
    }

    void solve() {
        int n = in.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) a[i] = in.nextInt();
        int[] b = a.clone();
        Arrays.sort(b);
        int[] ans = new int[n];
        for (int i = n-1; i >=0; i--) {
            int max = 0;
            for (int j = 0; j < a.length; j++) {
                while (j != 0 && a[j - 1] - a[j] >= 2) {
                    a[j - 1]--;
                    a[j]++;
                }
                while (j != 0 && a[j] - a[j - 1] >= 2) {
                    a[j - 1]++;
                    a[j]--;
                }
            }
            for (int j = 0; j < a.length; j++) {
                if (a[max] < a[j]) max = j;
            }
            ans[i] = a[max];
            a = rem(a, max);
        }

        if (Arrays.equals(ans,b)) System.out.println("YES");
        else System.out.println("NO");
    }

    void run() throws IOException {
        InputStream input = System.in;
        OutputStream output = System.out;
        try {
            File f = new File(filename + ".in");
            if (f.exists() && f.canRead()) {
                input = new FileInputStream(f);
                output = new FileOutputStream(filename + ".out");
            }
        } catch (IOException e) {
        }
        in = new FastScanner(input);
        out = new PrintWriter(new BufferedOutputStream(output));
        solve();
        in.close();
        out.close();
    }

    public static void main(String[] args) throws IOException {
        Locale.setDefault(Locale.US);
        new Alpha().run();
    }

    class FastScanner implements Closeable {
        private BufferedReader br;
        private StringTokenizer tokenizer;

        public FastScanner(InputStream stream) throws FileNotFoundException {
            br = new BufferedReader(new InputStreamReader(stream));
        }

        public boolean hasNext() {
            while (tokenizer == null || !tokenizer.hasMoreTokens()) {
                try {
                    String s = br.readLine();
                    if (s == null) {
                        return false;
                    }
                    tokenizer = new StringTokenizer(s);
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return tokenizer.hasMoreTokens();
        }

        public String next() {
            while (tokenizer == null || !tokenizer.hasMoreTokens()) {
                try {
                    tokenizer = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return tokenizer.nextToken();
        }

        public String nextLine() {
            if (tokenizer == null || !tokenizer.hasMoreTokens()) {
                try {
                    return br.readLine();
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return tokenizer.nextToken("\n");
        }

        public int nextInt() {
            return Integer.parseInt(next());
        }

        public long nextLong() {
            return Long.parseLong(next());
        }

        public double nextDouble() {
            return Double.parseDouble(next());
        }

        @Override
        public void close() throws IOException {
            br.close();
        }

        public byte nextByte() {
            return Byte.parseByte(next());
        }
    }
}

