import java.io.*;
import java.util.*;

public class AF2018_RavioliSort {

    private static final String YES = "YES";
    private static final String NO = "NO";

    public static void main(String[] args) {
        InputReader reader = new InputReader(System.in);
        PrintWriter writer = new PrintWriter(System.out);

        int len = reader.nextInt();

        List<Integer> ls = new LinkedList<>();


        int added = reader.nextInt();
        ls.add(added);

        int[] elem = new int[len];

        elem[0] = added;

        for (int i = 1; i < len; i++) {
            added = reader.nextInt();

            if(Math.abs(ls.get(i-1)-added) >= 2){
                writer.println(NO);
                writer.flush();
                return;
            }
            ls.add(added);
            elem[i] = added;
        }

        Arrays.sort(elem);

        int index;
        for (int i = len - 1; i >= 0; i--) {
            index = ls.indexOf(elem[i]);

            if(index == ls.size()-1 || index == 0){
                ls.remove(index);
            }else if(Math.abs(ls.get(index-1) - ls.get(index+1))>= 2){
                writer.println(NO);
                writer.flush();
                return;
            }else{
                ls.remove(index);
            }

        }

        writer.println(YES);
        writer.flush();
        writer.close();


    }

    static class InputReader {
        public BufferedReader reader;
        public StringTokenizer tokenizer;

        public InputReader(InputStream stream) {
            reader = new BufferedReader(new InputStreamReader(stream), 32768);
            tokenizer = null;
        }

        public String next() {
            while (tokenizer == null || !tokenizer.hasMoreTokens()) {
                try {
                    tokenizer = new StringTokenizer(reader.readLine());
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return tokenizer.nextToken();
        }

        public int nextInt() {
            return Integer.parseInt(next());
        }


    }
}

